module Main
where

import Data.List
import Data.Ord

popularLetters strings pos = pops
	where sorted = (group (sort (map (!! pos) strings)))
	      pops = nub (concat (reverse (sortBy (comparing length) sorted)))

-- cut down the dictionary to use only words of the length you're working with
smallDict num = filter (\x-> (length x) == num) dict

-- gets all the letters considered for a rotor
considered rotors pos num = cons
	where words = smallDict rotors
	      cons = take num (popularLetters words pos)


-- combinations method borrowed from Peter Hall, at 
-- http://www.haskell.org/pipermail/beginners/2011-November/008991.html
combinations 0 _ = [[]]
combinations _ [] = []
combinations n xs@(y:ys)
  | n < 0     = []
  | otherwise = case drop (n-1) xs of
                  [ ] -> []
                  [_] -> [xs]
                  _   -> [y:c | c <- combinations (n-1) ys]
                            ++ combinations n ys

--generates a given rotor
rotors numRotors numChar pos numCon = (combinations numChar (considered numRotors pos numCon))
-- : (rotors numRotors numChar (pos-1) numCon)

-- puts all possible rotors together into one list
allRotors numRotors numChar 0 numCon = [rotors numRotors numChar 0 numCon]
allRotors numRotors numChar pos numCon = (rotors numRotors numChar pos numCon) : (allRotors  numRotors numChar (pos-1) numCon)

--takes a list of lists of rotors and generates every possible configuration
allConfigs lst = sequence (reverse lst)

--takes a list of all possible configurations and computes the configuration
--that results in the most words
configuration lst rotors = maximumBy (comparing (numValidWords rotors)) lst

-- takes a configuration and figures out the valid words it conatains by comparing
-- them with a shortened dictionary
validWords rotors lst = valids
	where words = smallDict rotors
	      valids = filter (\x->elem x words) (sequence lst)

--takes a list of strings and counts the number of valid words
numValidWords rotors lst = length (validWords rotors lst)

-- all together now
result w x y z = configuration (allConfigs (allRotors w x y z)) w

